@Composable
fun DealOfTheDayView() {

    val productImages = listOf(
        "data:image/jpeg;base64,",
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQUihIebbnjJEPP4IXpvcjtmEGBCBzajvColQ&usqp=CAU",
        "data:image/jpeg;base64,",
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQUihIebbnjJEPP4IXpvcjtmEGBCBzajvColQ&usqp=CAU",
        "data:image/jpeg;base64,",
    )

    A_Column(
        modifier = Modifier
            .fillMaxWidth()
            .padding(8.dp),
        verticalArrangement = Arrangement.spacedBy(4.dp)
    ) {
        SectionTitleWithClickableItem(
            title = stringResource(id = R.string.deal_of_the_day),
            clickableText = stringResource(id = R.string.see_all)
        )

        A_Image(
            url = "https://photographylife.com/wp-content/uploads/2020/12/Apple-MacBook-Air-M1.jpg",
            contentScale = ContentScale.FillWidth
        )

        A_Text(text = "Rs. 99,990", config = getA_TextConfig(fontSize = 16.sp))

        Text(
            text = "Apple MacBook Pro 13 inch with M1 chip",
            style = MaterialTheme.typography.displaySmall,
            overflow = TextOverflow.Ellipsis,
            maxLines = 2
        )

        LazyRow {
            items(items = productImages) {
                A_Image(
                    url = it,
                    modifier = Modifier.size(100.dp),
                    contentScale = ContentScale.FillWidth
                )
            }
        }
    }
}
